import React from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route
} from 'react-router-dom';


import IndexPage from './pages/Index.js';
import MembershipsPage from './pages/Memberships.js';

import { ChainId, DAppProvider, AvalancheTestnet, Config, } from "@usedapp/core";
import { useEthers } from "@usedapp/core";
import { providers } from "ethers"

const avaxProvider = new providers.JsonRpcProvider("https://api.avax-test.network/ext/bc/C/rpc")
avaxProvider.pollingInterval = 10_000;

const config: Config = {
  readOnlyChainId: AvalancheTestnet.chainId,
  readOnlyUrls: {
    [AvalancheTestnet.chainId]: avaxProvider,
  },
  pollingInterval: 10000,
  notifications: {
    expirationPeriod: 1000,
    checkInterval: 1000
  }
}

export default function App() {
  return (

    <Router>
        <Switch>
          <Route exact path="/">
            <IndexPage />
          </Route>
          <Route exact path="/memberships">
            <MembershipsPage />
          </Route>
        </Switch>
    </Router>
  );
}
